const chalk = require( 'chalk' );

function log( ) {
	this.debug = false;
}

const ERROR = chalk.reset.inverse.bold.red( ' ERROR ' );
const DEBUG = chalk.reset.inverse.bold.yellow( ' DEBUG ' );

// setDebug
log.prototype.setDebug = function( status ){
	this.debug = status;
}

// write
log.prototype.write = function( msg ) {
	process.stdout.write( msg + '\n' );
}

// debug
log.prototype.debug = function( msg ){
	if ( this.debug ) {
		return;
	}
	this.write( DEBUG + msg );
}

// error. Danger Will Robinson
log.prototype.error = function ( msg ){
	this.write( ERROR + msg );
}


const Log = new log();
module.exports = Log
